/*
 * This function takes in an input string and a search term. 
 * 
 * It then splits the input string into an array of lines, 
 * finds the index of the line that contains the search term, 
 * and returns the next two lines after the matched line, 
 * separated by newlines. 
 * 
 * If the search term is not found or if there are not enough 
 * lines after the match, it returns null.
*/
function grepA2(inputString, searchTerm) {
    // Split the input string by newlines
    const lines = inputString.split('\n');
    // Find the index of the matched line
    const index = lines.findIndex(line => line.includes(searchTerm));

    if (index !== -1 && index < lines.length - 2) {
        // Return the next two lines after the matched line
        return lines.slice(index + 1, index + 3).join('\n');
    } else {
        // Return null if the searchTerm is not found or if there are not enough lines after the match
        return null;
    }
}

